mapper.update:
update ${bean.tableName}
<#list bean.fields as field> <if test="${field.fieldJavaName} != null <#if field.javaType=="java.lang.String">and ${field.fieldJavaName}!=''</#if>" >${field.fieldName} = _#_{${field.fieldJavaName}}, </#list>
where <#list bean.fields as field><#if field.key=="1">${field.fieldName} = _#_{${field.fieldJavaName}}</#if></#list>

mapper.select:
select  from ${bean.tableName} 
where flag = 1 
<#list bean.fields as field>
<#if field.isLike=='1'> and ${field.fieldName} like concat('%', _#_{${field.fieldJavaName}},'%') <#else> and ${field.fieldName} = _#_{${field.fieldJavaName}} </#if>
</#list>
${orderby}
limit _#_{begin},_#_{pageSize}

select  from ${bean.tableName} 
where flag = 1 
<#list bean.fields as field>
<#if field.isLike=='1'> and ${field.fieldName} like concat('%', _#_{${field.fieldJavaName}},'%') <#else> and ${field.fieldName} = _#_{${field.fieldJavaName}} </#if>
</#list>
${orderby}

select count(1) from ${bean.tableName}
where flag = 1 
<#list bean.fields as field><#list field.limitType?split("-") as limitType><#if limitType=='6'><if test="${field.fieldJavaName} != null <#if field.javaType=="java.lang.String">and ${field.fieldJavaName}!=''</#if>" >and ${field.fieldName} = _#_{${field.fieldJavaName}}</#if></#list><#if field.key=='1'><if test="${field.fieldJavaName} != null <#if field.javaType=="java.lang.String">and ${field.fieldJavaName}!=''</#if>" >and ${field.fieldName} != _#_{${field.fieldJavaName}}</#if></#list>
